INSERT INTO `cinema_v4`.`noticias` (`idnoticias`, `tituloNoticia`, `subtitulo`, `texto`, `dataPost`, `horaPost`, `autorPost`, `tipo`, `relac_games`, `relac_filmes`, `relac_series`, `img`, `destaque`, `imgDestaque`) VALUES (NULL, 'God of War é o exclusivo da PlayStation que teve maior lançamento da história nos EUA', 'Além de ser o exclusivo de PlayStation 4 que vendeu mais rápido na história do console.', 'God of War agora é o game da marca PlayStation -- incluindo todos os títulos de todos os consoles, desde o lançamento do primeiro console em 1995, no Ocidente -- que teve o maior lançamento, nos Estados Unidos. As informações são do grupo de pesquisas NPD (via IGN).', '20180523', NULL, 1, 2, 1, NULL, NULL, 'gow.jpg', 'on', 'gow_destaque.jpg');
INSERT INTO `cinema_v4`.`noticias` (`idnoticias`, `tituloNoticia`, `subtitulo`, `texto`, `dataPost`, `horaPost`, `autorPost`, `tipo`, `relac_games`, `relac_filmes`, `relac_series`, `img`, `destaque`, `imgDestaque`) VALUES (NULL, 'Se correr o bicho pega, se ficar o zumbi come', 'As pessoas são o aspecto mais importante de qualquer comunidade.', 'Trabalhar pelo bem do grupo é o papel dos líderes incumbidos da árdua tarefa de criar um ambiente estável e próspero, no qual todos podem não apenas sobreviver, mas se sentirem motivados também a conquistar.', '20180521', NULL, 1, 2, 2, NULL, NULL, 'decay2.jpg', 'on', 'decay_2_destaque.jpg');
INSERT INTO `cinema_v4`.`noticias` (`idnoticias`, `tituloNoticia`, `subtitulo`, `texto`, `dataPost`, `horaPost`, `autorPost`, `tipo`, `relac_games`, `relac_filmes`, `relac_series`, `img`, `destaque`, `imgDestaque`) VALUES (NULL, 'Vingadores: Guerra Infinita | Novas imagens em alta resolução focam na jornada de Thor', 'Filme continua em cartaz nos cinemas', 'O Marvel Studios divulgou novas imagens em alta resolução de Vingadores: Guerra Infinita. Confira (via CBM): O Marvel Studios divulgou novas imagens em alta resolução de Vingadores: Guerra Infinita. Confira (via CBM): O Marvel Studios divulgou novas imagens em alta resolução de Vingadores: Guerra Infinita. Confira (via CBM):', '20180523', NULL, 1, 1, NULL, 1, NULL, 'avengers_3_news4.jpg', 'on', 'avengers_3_news4.jpg');
INSERT INTO `cinema_v4`.`noticias` (`idnoticias`, `tituloNoticia`, `subtitulo`, `texto`, `dataPost`, `horaPost`, `autorPost`, `tipo`, `relac_games`, `relac_filmes`, `relac_series`, `img`, `destaque`, `imgDestaque`) VALUES (NULL, 'Handmaids Tale: atriz conta que aprova cenas de nudez para irem ao ar', 'Elisabeth Moss revelou que assiste a todas as cenas de nudez antes da série The Handmaid’s Tale ir ao ar na plataforma Hulu, nos Estados Unidos.', 'A produção, que já teve a sua terceira temporada confirmada antes mesmo do fim da exibição da segunda, possui algumas cenas de sexo — em alguns casos, consensual, e em outros, não. O aval de Elisabeth para a transmissão das gravações está previsto no contrato da atriz, que também é uma das produtoras da série.', '20180525', NULL, 1, 3, NULL, NULL, 2, 'handmaids.jpg', 'on', 'handmaids_destaque.jpg');
INSERT INTO `cinema_v4`.`noticias` (`idnoticias`, `tituloNoticia`, `subtitulo`, `texto`, `dataPost`, `horaPost`, `autorPost`, `tipo`, `relac_games`, `relac_filmes`, `relac_series`, `img`, `destaque`, `imgDestaque`) VALUES (NULL, 'Jurassic World: Reino Ameaçado deve ter estreia muito abaixo do original', 'Jurassic World: Reino Ameaçado pode estar mesmo é ameaçado nas bilheterias! Pelo menos é o que apontam as primeiras previsões de bilheteria.', 'De acordo com o Deadline, a aguardada sequência de \'Jurassic World\' deve arrecadar entre US$ 130 e US$ 150 milhões em seu primeiro fim de semana nos EUA.', '20180601', NULL, 1, 1, NULL, 3, NULL, 'jurassicworld2.jpg', 'off', NULL);
INSERT INTO `cinema_v4`.`noticias` (`idnoticias`, `tituloNoticia`, `subtitulo`, `texto`, `dataPost`, `horaPost`, `autorPost`, `tipo`, `relac_games`, `relac_filmes`, `relac_series`, `img`, `destaque`, `imgDestaque`) VALUES (NULL, 'Deadpool 2 | Ator discute a possibilidade de união com Universo Cinematográfico Marvel', 'Stefan Kapicic, ator de Colosso, discutiu recentemente a possibilidade de a franquia da Deadpool se mudar para o Universo Cinematográfico da Marvel, caso o acordo com da Fox com Disney seja finalizado.', '“Veremos. Você sabe que essas são as perguntas que não podemos responder porque não sabemos o que vai acontecer. Mas você sabe, Deadpool é uma franquia de sucesso, então, quem quer que assuma, eu não acho que eles vão mudar nada – e eu não quero mudar porque você sabe que Deadpool é Deadpool”, disse Kapicic. “Precisa ficar com sua classificação etária. Você não quer destruir algo que criou tanto dinheiro e, antes de mais nada, precisa satisfazer os fãs. Então, se eles cometem esse erro, eles vão (a voz de Colossus) precisam lutar sujo.” Um fato curioso de Deadpool 2 é que o filme tem a primeira trilha sonora com o selo de “conteúdo obsceno” da história – saiba mais.', '20180601', NULL, 1, 1, NULL, 4, NULL, 'deadpool_colosso_news.jpg', 'off', NULL);
INSERT INTO `cinema_v4`.`noticias` (`idnoticias`, `tituloNoticia`, `subtitulo`, `texto`, `dataPost`, `horaPost`, `autorPost`, `tipo`, `relac_games`, `relac_filmes`, `relac_series`, `img`, `destaque`, `imgDestaque`) VALUES (NULL, 'Anthony Hopkins pode ter dado spoilers de Westworld no mês passado (mas ninguém entendeu)', 'No mês passado, Anthony Hopkins publicou um vídeo que assustou a todos nós mostrando ele loucasso em sua casa.', 'O que ninguém percebeu é que o vídeo pode ter sido um spoiler do que está por vir na segunda temporada de Westworld. Isso porque o ator postou recentemente um segundo vídeo, que tem ligação direta com ele loucasso em casa e a série da HBO. E por fim, a ligação com Westworld está no piano — que é um dos símbolos mais marcantes da produção — e no episódio mais recente da série, “Phase Space”.', '20180601', NULL, 1, 3, NULL, NULL, 3, 'westworld_news1.jpg', 'off', NULL);
INSERT INTO `cinema_v4`.`noticias` (`idnoticias`, `tituloNoticia`, `subtitulo`, `texto`, `dataPost`, `horaPost`, `autorPost`, `tipo`, `relac_games`, `relac_filmes`, `relac_series`, `img`, `destaque`, `imgDestaque`) VALUES (NULL, 'Dark Souls Remastered traz melhorias visuais e legendas em português', 'Remasterização respeita as origens do clássico e corrige deslizes técnicos', 'Dark Souls Remastered é a versão aprimorada do clássico jogo da From Software. Com versões para PS4, Xbox One e PC via Steam, - posteriormente receberá uma versão para Nintendo Switch -, o título mantém a essência do original, mas traz melhorias técnicas bem-vindas aos fãs. Confira as impressões iniciais: Na primeira aventura da série de RPG e ação, o jogador deve explorar a distorcida região de Lordran, terra dos senhores antigos e palco de confrontos colossais contra demônios, feras e armadilhas em uma nova era de Fogo e Escuridão. ', '20180606', NULL, 1, 2, 5, NULL, NULL, 'darksouls_news1.jpg', 'off', NULL);
INSERT INTO `cinema_v4`.`noticias` (`idnoticias`, `tituloNoticia`, `subtitulo`, `texto`, `dataPost`, `horaPost`, `autorPost`, `tipo`, `relac_games`, `relac_filmes`, `relac_series`, `img`, `destaque`, `imgDestaque`) VALUES (NULL, 'Game of Thrones | Jason Momoa se reúne com o elenco para comemorar temporada final ', 'Ator expressou sua gratidão no Instagram ', 'Mesmo tendo participado apenas da primeira temporada, Jason Momoa continua sendo um enorme fã de Game of Thrones - como já demonstrou diversas vezes no passado. Agora, o ator de Khal Drogo foi até a Irlanda - onde está sendo gravada a temporada final - para encontrar o elenco - veja a galeria abaixo: No Instagram, Momoa compartilhou sua felicidade: \"Sou grato de ter sido parte do maior programa da Terra. Estou honrado de poder ir até a Irlanda e celebrar com os meus amigos. Estou aqui apenas pelos beijos e abraços\", brincou. Ele continua agradecendo os criadores do seriado: \"Mahalo, David Benioff e Dan Weiss, vocês são lendas. Parabéns a talentosa equipe e elenco.\"', '20180606', NULL, 1, 3, NULL, NULL, 4, 'got_news1.jpg', 'on', 'got_destaque.jpg');
INSERT INTO `cinema_v4`.`noticias` (`idnoticias`, `tituloNoticia`, `subtitulo`, `texto`, `dataPost`, `horaPost`, `autorPost`, `tipo`, `relac_games`, `relac_filmes`, `relac_series`, `img`, `destaque`, `imgDestaque`) VALUES (NULL, 'Fortnite já foi baixado por 2 milhões de jogadores no Switch', 'Será que fez sucesso?', '<p><a href=\"http://br.ign.com/fortnite/62534/news/fortnite-confira-como-funciona-o-carrinho-de-supermercado-no?utm_source=recirc\" target=\"_self\">Fortnite</a> foi anunciado e lan&ccedil;ado para <a href=\"http://br.ign.com/fortnite/62587/news/fortnite-e-dragon-ball-fighterz-podem-ser-lancados-para-nint?utm_source=recirc\" target=\"_self\">Switch</a> durante a confer&ecirc;ncia da <a href=\"http://br.ign.com/fortnite/62587/news/fortnite-e-dragon-ball-fighterz-podem-ser-lancados-para-nint?utm_source=recirc\" target=\"_self\">Nintendo</a>, na &uacute;ltima ter&ccedil;a-feira (12), e em pouco mais de um dia o game j&aacute; foi baixado por 2 milh&otilde;es de jogadores.</p>', '20180613', NULL, 1, 2, 8, NULL, NULL, 'fortnite_news1.jpg', 'off', NULL);
INSERT INTO `cinema_v4`.`noticias` (`idnoticias`, `tituloNoticia`, `subtitulo`, `texto`, `dataPost`, `horaPost`, `autorPost`, `tipo`, `relac_games`, `relac_filmes`, `relac_series`, `img`, `destaque`, `imgDestaque`) VALUES (NULL, 'Entrevista: Quais novidades realmente importam em FIFA 19?', 'A diferença está nos detalhes', '<p>H&aacute; mais de 25 anos, a Electronic Arts tem o costume de lan&ccedil;ar um novo game FIFA por ano. E cada nova vers&atilde;o do simulador futebol&iacute;stico tem como miss&atilde;o principal superar a anterior, tanto em qualidade t&eacute;cnica e avan&ccedil;os tecnol&oacute;gicos como na receptividade dos f&atilde;s hardcore. &Eacute; uma tarefa &aacute;rdua, que a EA parece aceitar de bom grado -- e os esfor&ccedil;os trazem resultados. FIFA &eacute; ainda uma das franquias de games mais vendidas no Brasil, mesmo com cada novo jogo trazendo evolu&ccedil;&otilde;es pouco percept&iacute;veis em rela&ccedil;&atilde;o a do ano que ficou para tr&aacute;s. Para falar sobre as diversas novidades de FIFA 19, o IGN Brasil conversou com Santiago Jaramillo, diretor criativo do game, durante o evento EA Play. Assista &agrave; entrevista abaixo, ou leia a transcri&ccedil;&atilde;o completa a seguir.</p>', '20180613', NULL, 1, 2, 7, NULL, NULL, 'fifa19_news1.jpg', 'on', 'fifa19_destaque.jpg');
INSERT INTO `cinema_v4`.`noticias` (`idnoticias`, `tituloNoticia`, `subtitulo`, `texto`, `dataPost`, `horaPost`, `autorPost`, `tipo`, `relac_games`, `relac_filmes`, `relac_series`, `img`, `destaque`, `imgDestaque`) VALUES (NULL, 'Atlanta | 3ª temporada terá mais episódios com personagens femininas, diz Stephen Glover', '\"Há um ponto de vista muito particular que pensamos em explorar\", falou o produtor e roteirista', '<p>A terceira temporada de <em><strong><a href=\"https://omelete.com.br/busca/?q=Atlanta\">Atlanta</a></strong></em>, s&eacute;rie de TV de <strong>Donald Glover</strong> no <strong>FX</strong>, deve ter mais mulheres na tela. A informa&ccedil;&atilde;o foi divulgada durante um painel de considera&ccedil;&atilde;o para o <strong>Emmy 2018</strong>, com presen&ccedil;a do elenco e produ&ccedil;&atilde;o [via <em><a href=\"https://www.hollywoodreporter.com/live-feed/atlanta-season-3-feature-women-stories-writer-producer-stephen-glover-says-1118992\">Hollywood Reporter</a></em>].</p>', '20180613', NULL, 1, 3, NULL, NULL, 6, 'atlanta_news1.jpg', 'off', NULL);
INSERT INTO `cinema_v4`.`noticias` (`idnoticias`, `tituloNoticia`, `subtitulo`, `texto`, `dataPost`, `horaPost`, `autorPost`, `tipo`, `relac_games`, `relac_filmes`, `relac_series`, `img`, `destaque`, `imgDestaque`) VALUES (NULL, 'Westworld | Jornada do Homem de Preto continua nas fotos do nono episódio da 2ª temporada ', '\"Vanishing Point\" vai ao ar no próximo domingo (17) ', '<p>&quot;<strong>Vanishing Point</strong>&quot;, epis&oacute;dio nove da segunda temporada de <em><strong><a href=\"https://omelete.com.br/series-tv/westworld/\">Westworld</a></strong></em>, teve imagens in&eacute;ditas divulgadas pela <strong>HBO</strong> que mostram que a jornada do Homem de Preto (<strong>Ed Harris</strong>) continua - veja abaixo:</p>', '20180613', NULL, 1, 3, NULL, NULL, 3, 'westworld_news1.jpg', 'off', NULL);

